  public async append(
    event: Event | Event[],
    expectedVersion: ExpectedVersion | number | Long = -2,
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<WriteResult> {
    if (Array.isArray(event)) {
      return await this.appendEvents(event, expectedVersion, requireMaster, credentials)
    } else {
      return await this.appendEvents([event], expectedVersion, requireMaster, credentials)
    }
  }

  /**
   * Hard deletes a stream - stream with same name can not be used in future
   */
  public async hardDelete(
    expectedVersion: ExpectedVersion = ExpectedVersion.Any,
    requireMaster?: boolean
  ): Promise<Position> {
    this.log.debug(`Hard delete Stream ${this.streamId}`)
    return await this.delete(true, expectedVersion, requireMaster)
  }

  /**
   * Soft deletes a stream - stream with same name can be used in future and indexes are preserved
   */
  public async softDelete(
    expectedVersion: ExpectedVersion = ExpectedVersion.Any,
    requireMaster?: boolean
  ): Promise<Position> {
    this.log.debug(`Soft delete Stream ${this.streamId}`)
    return await this.delete(false, expectedVersion, requireMaster)
  }

  /**
   * Delete a stream - can't be called directly
   * Use softDelete or hardDelete instead
   */
  protected delete(
    hardDelete: boolean,
    expectedVersion: ExpectedVersion = ExpectedVersion.Any,
    requireMaster?: boolean,
    credentials?: UserCredentials
  ): Promise<Position> {
    if (this.isMetaStream()) {
      throw eventstoreError.newBadRequestError(
        `You can not delete metadata stream ${this.streamId}`
      )
    }
    if (requireMaster === undefined) {
      requireMaster = this.options.requireMaster
    }
    return new Promise(
      (resolve, reject): void => {
        const raw = protobuf.DeleteStream.fromObject({
          eventStreamId: this.streamId,
          expectedVersion,
          requireMaster,
          hardDelete
        })
        this.esConnection
          .getConnection()
          .sendCommand(
            uuid(),
            EventstoreCommand.DeleteStream,
            Buffer.from(protobuf.DeleteStream.encode(raw).finish()),
            credentials || this.options.credentials,
            {
              resolve,
              reject
            }
          )
      }
    )
  }

  /**
   * Get event at specified position from stream
   */
  public async getEventByNumber(
    eventNumber: Long | number,
    resolveLinks: boolean = true,
    requireMaster?: boolean,
    credentials?: UserCredentials
  ): Promise<Event | null> {
    if (requireMaster === undefined) {
      requireMaster = this.options.requireMaster
    }
    const result: model.eventstore.proto.IResolvedIndexedEvent = await new Promise(
      (resolve, reject): void => {
        const raw = protobuf.ReadEvent.fromObject({
          eventStreamId: this.streamId,
          eventNumber: eventNumber,
          resolveLinkTos: resolveLinks,
          requireMaster: requireMaster
        })
        this.esConnection
          .getConnection()
          .sendCommand(
            uuid(),
            EventstoreCommand.ReadEvent,
            Buffer.from(protobuf.ReadEvent.encode(raw).finish()),
            credentials || this.options.credentials,
            {
              resolve,
              reject
            }
          )
      }
    )

    if (!result.event && !result.link) {
      return null
    }
    return Event.fromRaw(result.event || result.link)
  }

  /**
   * Returns first event from stream
   */
  public async getFirstEvent(
    resolveLinks: boolean = true,
    requireMaster?: boolean,
    credentials?: UserCredentials
  ): Promise<Event | null> {
    return await this.getEventByNumber(
      StreamPosition.Start,
      resolveLinks,
      requireMaster,
      credentials
    )
  }

  /**
   * Returns last event from stream
   */
  public async getLastEvent(
    resolveLinks: boolean = true,
    requireMaster?: boolean,
    credentials?: UserCredentials
  ): Promise<Event | null> {
    return await this.getEventByNumber(StreamPosition.End, resolveLinks, requireMaster, credentials)
  }

  /**
   * Returns stream metadata if set or
   */
  public async getMetadata(
    requireMaster?: boolean,
    credentials?: UserCredentials
  ): Promise<
    | {
        $correlationId?: string
        $causationId?: string
      } & {[k: string]: JSONValue}
    | null
  > {
    if (this.isMetaStream()) {
      throw eventstoreError.newBadRequestError(
        `You can not get metadata of metadata stream ${this.streamId}`
      )
    }
    if (requireMaster === undefined) {
      requireMaster = this.options.requireMaster
    }
    try {
      const result = await this.esConnection
        .fromStream(`$$${this.streamId}`, {
          resolveLinks: false,
          requireMaster,
          credentials: credentials || this.options.credentials
        })
        .getLastEvent()

      if (result) {
        result.freeze()
        return {...result.data}
      } else return null
    } catch (err) {
      if (err.name === 'EventstoreNoStreamError') {
        return null
      }
      throw err
    }
  }

  /**
   * Set metadata for stream
   */
  public async setMetadata(
    newMetadata: {},
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<void> {
    if (this.isMetaStream()) {
      throw eventstoreError.newBadRequestError(
        `You can not set metadata for metadata stream ${this.streamId}`
      )
    }
    if (requireMaster === undefined) {
      requireMaster = this.options.requireMaster
    }
    const newMetaEvent = new Event('$metadata', newMetadata)
    await this.esConnection
      .fromStream(`$$${this.streamId}`, {
        resolveLinks: false,
        requireMaster,
        credentials: credentials || this.options.credentials
      })
      .append(
        newMetaEvent,
        ExpectedVersion.Any,
        requireMaster,
        credentials || this.options.credentials
      )
  }

  /**
   * Creates a new instance of {Transaction} for current stream
   */
  public async startTransaction(
    expectedVersion: ExpectedVersion = ExpectedVersion.Any,
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<Transaction> {
    if (this.isMetaStream()) {
      throw eventstoreError.newBadRequestError(
        `Transactions fpr metadata stream ${this.streamId} not supported`
      )
    }
    if (requireMaster === undefined) {
      requireMaster = this.options.requireMaster
    }
    const transactionId: Long = await new Promise(
      (resolve, reject): void => {
        const raw = protobuf.TransactionStart.fromObject({
          eventStreamId: this.streamId,
          expectedVersion: expectedVersion,
          requireMaster
        })
        this.esConnection
          .getConnection()
          .sendCommand(
            uuid(),
            EventstoreCommand.TransactionStart,
            Buffer.from(protobuf.TransactionStart.encode(raw).finish()),
            credentials || this.options.credentials,
            {
              resolve,
              reject
            }
          )
      }
    )

    return new Transaction(this, transactionId, this.esConnection, requireMaster, credentials)
  }

  /**
   * Reads a slice from current stream in given direction starting at given position
   */
  protected async readSlice(
    direction: EventstoreCommand,
    fromEventNumber: number | Long = 0,
    maxCount: number = 100,
    resolveLinkTos: boolean = true,
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<model.eventstore.proto.ReadStreamEventsCompleted> {
    if (requireMaster === undefined) {
      requireMaster = this.options.requireMaster
    }
    return await new Promise(
      (resolve, reject): void => {
        const raw = protobuf.ReadStreamEvents.fromObject({
          eventStreamId: this.streamId,
          fromEventNumber,
          maxCount,
          resolveLinkTos,
          requireMaster
        })
        this.esConnection
          .getConnection()
          .sendCommand(
            uuid(),
            direction,
            Buffer.from(protobuf.ReadStreamEvents.encode(raw).finish()),
            credentials || this.options.credentials,
            {
              resolve,
              reject
            }
          )
      }
    )
  }

  /**
   * Read a slice from stream in forward direction starting at given position
   */
  public async readSliceForward(
    fromEventNumber: number | Long = StreamPosition.Start,
    maxCount: number = 100,
    resolveLinkTos: boolean = true,
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<model.eventstore.proto.ReadStreamEventsCompleted> {
    return await this.readSlice(
      EventstoreCommand.ReadStreamEventsForward,
      fromEventNumber,
      maxCount,
      resolveLinkTos,
      requireMaster,
      credentials
    )
  }

  /**
   * Read a slice from stream in backward direction starting at given position
   */
  public async readSliceBackward(
    fromEventNumber: number | Long = StreamPosition.End,
    maxCount: number = 100,
    resolveLinkTos: boolean = true,
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<model.eventstore.proto.ReadStreamEventsCompleted> {
    return await this.readSlice(
      EventstoreCommand.ReadStreamEventsBackward,
      fromEventNumber,
      maxCount,
      resolveLinkTos,
      requireMaster,
      credentials
    )
  }
  // eslint-disable-next-line @typescript-eslint/explicit-function-return-type
  protected async walkStream(
    forward: boolean = true,
    start: Long | number = StreamPosition.Start,
    maxCount: number = 100,
    resolveLinkTos: boolean = true,
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ) {
    const that = this
    const getSlice = forward ? 'readSliceForward' : 'readSliceBackward'
    if (requireMaster === undefined) {
      requireMaster = this.options.requireMaster
    }
    if (credentials === undefined) {
      credentials = this.options.credentials
    }
    // eslint-disable-next-line @typescript-eslint/explicit-function-return-type
    const asyncGenerator = async function*(begin: Long | number) {
      let index = 0
      //fetch first slice
      let readResult = that[getSlice](begin, maxCount, resolveLinkTos, requireMaster, credentials)
      let result = await readResult
      if (!result.isEndOfStream) {
        //we have more so start fetching in background
        begin = result.nextEventNumber
        readResult = that[getSlice](begin, maxCount, resolveLinkTos, requireMaster, credentials)
      }
      while (true) {
        if (index < result.events.length) {
          const entry = result.events[index++]
          yield Event.fromRaw(entry.event || entry.link)
        } else if (result.isEndOfStream) {
          return null
        } else {
          index = 0
          //wait for background fetch and grab result
          result = await readResult
          if (!result.isEndOfStream) {
            //if there are more events start fetching in background
            begin = result.nextEventNumber
            readResult = that[getSlice](begin, maxCount, resolveLinkTos, requireMaster, credentials)
          }
        }
      }
    }

    return new StreamWalker(asyncGenerator(start))
  }

  public async walkStreamForward(
    start: Long | number = StreamPosition.Start,
    maxCount: number = 100,
    resolveLinkTos: boolean = true,
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<StreamWalker> {
    return await this.walkStream(true, start, maxCount, resolveLinkTos, requireMaster, credentials)
  }

  public async walkStreamBackward(
    start: Long | number = StreamPosition.End,
    maxCount: number = 100,
    resolveLinkTos: boolean = true,
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<StreamWalker> {
    return await this.walkStream(false, start, maxCount, resolveLinkTos, requireMaster, credentials)
  }

  /**
   * Subscribe to current stream and return a subscription
   */
  public async subscribe(
    resolveLinkTos: boolean = true,
    credentials?: UserCredentials | null
  ): Promise<Subscription> {
    return await this.esConnection
      .getConnection()
      .subscribeToStream(this, resolveLinkTos, credentials || this.options.credentials || null)
  }

  /**
   * Creates a persistent subscription for current stream
   * This operation needs admin rights and a master connection
   */
  public async createPersistentSubscription(
    subscriptionGroupName: string,
    customConfig: PersistentSubscriptionConfig | {} = {},
    credentials?: UserCredentials | null
  ): Promise<PersistentSubscription> {
    const settings = setPersistentSubscriptionConfig(customConfig)

    await new Promise(
      (resolve, reject): void => {
        const raw = protobuf.CreatePersistentSubscription.fromObject({
          subscriptionGroupName,
          eventStreamId: this.id,
          ...settings
        })
        this.esConnection
          .getConnection()
          .sendCommand(
            uuid(),
            EventstoreCommand.CreatePersistentSubscription,
            Buffer.from(protobuf.CreatePersistentSubscription.encode(raw).finish()),
            credentials || this.options.credentials,
            {
              resolve,
              reject
            }
          )
      }
    )
    return new PersistentSubscription(
      this,
      this.esConnection,
      this.options.credentials,
      subscriptionGroupName
    )
  }

  /**
   * Returns a instance of persistance subscription given by group name
   */
  public getPersistentSubscription(
    subscriptionGroupName: string,
    credentials?: UserCredentials | null
  ): PersistentSubscription {
    return new PersistentSubscription(
      this,
      this.esConnection,
      credentials || this.options.credentials,
      subscriptionGroupName
    )
  }
}

/** typescript enumeration for stream position constants */
export enum StreamPosition {
  Start = 0,
  End = -1
}

import {Stream} from './Stream'
import {Event} from '../event'
import uuid = require('uuid/v4')
import * as model from '../protobuf/model'
import {EventstoreCommand} from '../protobuf/EventstoreCommand'
import * as eventstoreError from '../errors'
import {UserCredentials} from '../eventstore/EventstoreSettings'
import {Eventstore} from '../eventstore'

const protobuf = model.eventstore.proto

/**
 * Base class for handling transaction writes to stream
 */
export class Transaction {
  /** id of transaction */
  protected transactionId: Long
  /** corresponding stream */
  protected stream: Stream
  /** current connection */
  protected esConnection: Eventstore
  /** indicates if transaction is committed */
  protected committed: boolean = false
  /** indicates if transaction is rolled back */
  protected rolledBack: boolean = false
  /** indicates if transaction needs master node */
  protected requireMaster: boolean
  /** credentials for transaction */
  protected credentials: UserCredentials | null = null

  /**
   *  Creates an instance of Transaction.
   */
  public constructor(
    stream: Stream,
    transactionId: Long,
    esConnection: Eventstore,
    requireMaster: boolean,
    credentials: UserCredentials | null = null
  ) {
    this.stream = stream
    this.transactionId = transactionId
    this.esConnection = esConnection
    this.requireMaster = requireMaster
    this.credentials = credentials
  }

  /**
   * Gets whether is committed
   */
  public get isCommitted(): boolean {
    return this.committed
  }

  /**
   * Gets whether is rolled back
   */
  public get isRolledBack(): boolean {
    return this.rolledBack
  }

  /**
   * Appends single event or array of events to transaction
   */
  public async append(
    event: Event | Event[],
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<void> {
    if (Array.isArray(event)) {
      return await this.appendEvents(event, requireMaster, credentials)
    } else {
      return await this.appendEvents([event], requireMaster, credentials)
    }
  }

  /**
   * Appends array of evens to transaction
   */
  protected async appendEvents(
    events: Event[],
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<void> {
    if (this.isCommitted) {
      throw eventstoreError.newInvalidTransactionError(
        `Transaction ${this.transactionId} is already committed`
      )
    }
    if (this.isRolledBack) {
      throw eventstoreError.newInvalidTransactionError(
        `Transaction ${this.transactionId} is already rolled back`
      )
    }
    if (requireMaster === undefined) {
      requireMaster = this.requireMaster
    }
    const eventArrayTransformed: model.eventstore.proto.NewEvent[] = events.map(
      (event): model.eventstore.proto.NewEvent => {
        if (!event.isNew()) {
          throw eventstoreError.newOperationError(
            `Event ${event.name} is already stored in eventstore`
          )
        }
        return event.toRaw()
      }
    )
    const raw = protobuf.TransactionWrite.fromObject({
      transactionId: this.transactionId,
      events: eventArrayTransformed,
      requireMaster: requireMaster
    })
    raw.transactionId = this.transactionId
    await new Promise(
      (resolve, reject): void => {
        const setToWritten = (id: Long): void => {
          events.forEach((event): void => event.freeze())
          resolve(id)
        }
        this.esConnection
          .getConnection()
          .sendCommand(
            uuid(),
            EventstoreCommand.TransactionWrite,
            Buffer.from(protobuf.TransactionWrite.encode(raw).finish()),
            credentials || this.credentials,
            {
              resolve: setToWritten,
              reject
            }
          )
      }
    )
  }

  /**
   * Commits transaction
   */
  public async commit(
    requireMaster?: boolean,
    credentials?: UserCredentials | null
  ): Promise<void> {
    if (this.isCommitted) {
      throw eventstoreError.newInvalidTransactionError(
        `Transaction ${this.transactionId} is already committed`
      )
    }
    if (this.isRolledBack) {
      throw eventstoreError.newInvalidTransactionError(
        `Transaction ${this.transactionId} is already roled back`
      )
    }
    if (requireMaster === undefined) {
      requireMaster = this.requireMaster
    }
    await new Promise(
      (resolve, reject): void => {
        const rejectFunction = (err: Error): void => {
          this.committed = true
          this.rolledBack = true
          reject(err)
        }
        const raw = protobuf.TransactionCommit.fromObject({
          transactionId: this.transactionId,
          requireMaster: requireMaster
        })
        this.esConnection
          .getConnection()
          .sendCommand(
            uuid(),
            EventstoreCommand.TransactionCommit,
            Buffer.from(protobuf.TransactionCommit.encode(raw).finish()),
            credentials || this.credentials,
            {
              resolve,
              reject: rejectFunction
            }
          )
      }
    )
    this.committed = true
  }

  /**
   * Roles back transaction
   */
  public roleBack(): void {
    this.rolledBack = true
  }

  /**
   * Gets transaction id
   */
  public get id(): Long {
    return this.transactionId
  }

  /**
   * Gets transaction name
   */
  public get name(): string {
    return 'Transaction: ' + this.transactionId
  }
}

import {Stream} from '../stream'
import {UserCredentials, Eventstore} from '../eventstore'
import {EventEmitter} from 'events'
import {EventstoreCommand} from '../protobuf/EventstoreCommand'
import * as model from '../protobuf/model'
import uuid = require('uuid/v4')
import {PersistentSubscriptionConfig, setPersistentSubscriptionConfig, SubscriptionStatus} from '.'
import Long from 'long'
import {Event} from '../event'
import {uuidToBuffer} from '../protobuf/uuidBufferConvert'

const protobuf = model.eventstore.proto

/**
 * Represents a persistent subscription
 */
export class PersistentSubscription extends EventEmitter {
  /** corresponding stream  */
  public stream: Stream
  /** connection to use */
  protected esConnection: Eventstore
  /** name of subscription */
  public subscriptionGroupName: string
  /** user credentials */
  protected credentials: UserCredentials | null = null

  public lastCommitPosition: Long = Long.fromNumber(0)
  public lastEventNumber: Long | null = null

  public id: string = uuid()

  public subscriptionId: string

  public allowedInFlightMessages: number = 10

  protected status: SubscriptionStatus = SubscriptionStatus.disconnected

  public autoAcknowledge: boolean = true

  /**
   * Creates an instance of persistent subscription.
   */
  public constructor(
    stream: Stream,
    esConnection: Eventstore,
    credentials: UserCredentials | null,
    subscriptionGroupName: string
  ) {
    super()
    this.stream = stream
    this.esConnection = esConnection
    this.subscriptionGroupName = subscriptionGroupName
    this.credentials = credentials

    this.subscriptionId = `${this.stream.id} :: ${this.subscriptionGroupName}`

    this.on(
      'dropped',
      (): void => {
        this.state = SubscriptionStatus.disconnected
      }
    )
  }

  /**
   * Gets name
   */
  public get name(): string {
    return `PersistentSubscription: ${this.stream.id} :: ${this.subscriptionGroupName}`
  }

  /**
   * Gets state
   */
  public get state(): SubscriptionStatus {
    return this.status
  }

  /**
   * Sets state
   */
  public set state(newStatus: SubscriptionStatus) {
    this.emit(SubscriptionStatus[newStatus])
    this.status = newStatus
  }

  /**
   * Connects persistent subscription
   */
  public async subscribe(
    allowedInFlightMessages: number = 10,
    credentials?: UserCredentials | null
  ): Promise<PersistentSubscription> {
    this.allowedInFlightMessages = allowedInFlightMessages
    if (credentials) {
      this.credentials = credentials
    }
    const result = await this.esConnection
      .getConnection()
      .connectToPersistentSubscription(this, allowedInFlightMessages, this.credentials)
    this.subscriptionId = result.subscriptionId
    this.state = SubscriptionStatus.connected
    return this
  }

  /**
   * Unsubscribe from stream
   */
  public async unsubscribe(credentials?: UserCredentials | null): Promise<void> {
    await this.esConnection
      .getConnection()
      .unsubscribeFromPersistentSubscription(this.id, credentials || this.credentials)
  }

  /**
   * Deletes persistent subscription
   */
  public async delete(credentials?: UserCredentials | null): Promise<PersistentSubscription> {
    const result: PersistentSubscription = await new Promise(
      (resolve, reject): void => {
        const raw = protobuf.UpdatePersistentSubscription.fromObject({
          subscriptionGroupName: this.subscriptionGroupName,
          eventStreamId: this.stream.id
        })
        this.esConnection
          .getConnection()
          .sendCommand(
            uuid(),
            EventstoreCommand.DeletePersistentSubscription,
            Buffer.from(protobuf.DeletePersistentSubscription.encode(raw).finish()),
            credentials || this.credentials,
            {
              resolve,
              reject
            }
          )
      }
    )
    this.state = SubscriptionStatus.disconnected
    return result
  }

  /**
   * Updates persistent subscription
   */
  public async update(
    customConfig: PersistentSubscriptionConfig | {} = {},
    credentials?: UserCredentials | null
  ): Promise<PersistentSubscription> {
    const settings = setPersistentSubscriptionConfig(customConfig)

    return await new Promise(
      (resolve, reject): void => {
        const raw = protobuf.UpdatePersistentSubscription.fromObject({
          subscriptionGroupName: this.subscriptionGroupName,
          eventStreamId: this.stream.id,
          ...settings
        })
        this.esConnection
          .getConnection()
          .sendCommand(
            uuid(),
            EventstoreCommand.UpdatePersistentSubscription,
            Buffer.from(protobuf.UpdatePersistentSubscription.encode(raw).finish()),
            credentials || this.credentials,
            {
              resolve,
              reject
            }
          )
      }
    )
  }

  /**
   * Acknowledges single event
   */
  public acknowledgeEvent(event: Event, credentials?: UserCredentials | null): void {
    return this.acknowledgeEvents([event], credentials)
  }

  /**
   * Acknowledges array of events
   */
  public acknowledgeEvents(events: Event[], credentials?: UserCredentials | null): void {
    const processedEventIds = events.map(
      (event): Buffer => {
        return uuidToBuffer(event.id)
      }
    )

    const raw = protobuf.PersistentSubscriptionAckEvents.fromObject({
      subscriptionId: this.subscriptionId,
      processedEventIds
    })
    this.esConnection
      .getConnection()
      .sendCommand(
        this.id,
        EventstoreCommand.PersistentSubscriptionAckEvents,
        Buffer.from(protobuf.PersistentSubscriptionAckEvents.encode(raw).finish()),
        credentials || this.credentials
      )
  }

  /**
   * Not acknowledge single event
   */
  public notAcknowledgeEvent(
    event: Event,
    reason: model.eventstore.proto.PersistentSubscriptionNakEvents.NakAction = model.eventstore
      .proto.PersistentSubscriptionNakEvents.NakAction.Unknown,
    message?: string,
    credentials?: UserCredentials | null
  ): void {
    return this.notAcknowledgeEvents([event], reason, message, credentials)
  }

  /**
   * Not acknowledge array of events
   */
  public notAcknowledgeEvents(
    events: Event[],
    reason: model.eventstore.proto.PersistentSubscriptionNakEvents.NakAction = model.eventstore
      .proto.PersistentSubscriptionNakEvents.NakAction.Unknown,
    message?: string,
    credentials?: UserCredentials | null
  ): void {
    const processedEventIds = events.map(
      (event): Buffer => {
        return uuidToBuffer(event.id)
      }
    )
    const raw = protobuf.PersistentSubscriptionNakEvents.fromObject({
      subscriptionId: this.subscriptionId,
      processedEventIds,
      message,
      action: reason
    })
    this.esConnection
      .getConnection()
      .sendCommand(
        this.id,
        EventstoreCommand.PersistentSubscriptionNakEvents,
        Buffer.from(protobuf.PersistentSubscriptionNakEvents.encode(raw).finish()),
        credentials || this.credentials
      )
  }
}

import {StreamPosition} from '../stream/StreamPosition'
import Long = require('long')

/** constant definition of consumer strategies */
export const SystemConsumerStrategies = {
  DispatchToSingle: 'DispatchToSingle',
  RoundRobin: 'RoundRobin',
  Pinned: 'Pinned'
}

/**
 * typescript interface for Persistent subscription config
 */
export interface PersistentSubscriptionConfig {
  resolveLinkTos: boolean
  startFrom: Long | number
  messageTimeoutMilliseconds: number
  recordStatistics: boolean
  maxRetryCount: number
  liveBufferSize: number
  bufferSize: number
  readBatchSize: number
  checkpointAfterTime: number
  checkpointMinCount: number
  checkpointMaxCount: number
  subscriberMaxCount: number
  namedConsumerStrategy: string
  preferRoundRobin: boolean
}

/** default persistent subscription config */
const defaultPersistentSubscriptionConfig: PersistentSubscriptionConfig = {
  resolveLinkTos: true,
  startFrom: StreamPosition.Start,
  messageTimeoutMilliseconds: 10000,
  recordStatistics: false,
  maxRetryCount: 10,
  liveBufferSize: 500,
  bufferSize: 500,
  readBatchSize: 20,
  checkpointAfterTime: 1000,
  checkpointMinCount: 10,
  checkpointMaxCount: 500,
  subscriberMaxCount: 10,
  namedConsumerStrategy: SystemConsumerStrategies.RoundRobin,
  preferRoundRobin: true
}

/**
 * merges given settings with default settings
 */
let setPersistentSubscriptionConfig = (
  customSettings: object | PersistentSubscriptionConfig
): PersistentSubscriptionConfig => {
  return {...defaultPersistentSubscriptionConfig, ...customSettings}
}

export {setPersistentSubscriptionConfig}

import {EventEmitter} from 'events'
import * as model from '../protobuf/model'
import {UserCredentials, Position} from '../eventstore'
import {Stream} from '../stream'
import {TCPConnection} from '../eventstore/TCPConnection'
import * as bunyan from 'bunyan'
import {Event} from '../event'

/**
 * Base class for handling subscriptions
 */
export class Subscription extends EventEmitter {
  /** uuid4 of subscription */
  protected subscriptionId: string
  /** indicates if subscription is running */
  public isSubscribed: boolean = false
  /** connection to use */
  protected tcpConnection: TCPConnection
  /** credentials for subscription */
  protected credentials: UserCredentials | null = null
  /** instance of corresponding stream */
  protected stream: Stream
  /** indicates if events should be full resolved */
  protected resolveLinkTos: boolean
  /** logger */
  public log: bunyan
  /** global log position */
  protected position: Position | null = null

  /**
   * Creates an instance of subscription.
   */
  public constructor(
    subscriptionId: string,
    tcpConnection: TCPConnection,
    stream: Stream,
    resolveLinkTos: boolean,
    credentials: UserCredentials | null
  ) {
    super()
    this.subscriptionId = subscriptionId
    this.tcpConnection = tcpConnection
    this.credentials = credentials
    this.stream = stream
    this.resolveLinkTos = resolveLinkTos
    this.on('subscribed', this.onSubscribed)
    this.on('dropped', this.onDropped)
    this.on('event', this.onEvent)
    this.on('error', this.onError)
    this.log = stream.log.child({module: 'Subscription', subscriptionId: this.id})
  }

  /**
   * Returns subscriptionId
   */
  public get id(): string {
    return this.subscriptionId
  }

  /**
   * Returns subscription nam
   */
  public get name(): string {
    return 'Subscription: ' + this.subscriptionId
  }

  /**
   * Gets get credentials
   */
  public get getCredentials(): UserCredentials | null {
    return this.credentials
  }

  /**
   * Gets resolve link tos
   */
  public getResolveLinkTos(): boolean {
    return this.resolveLinkTos
  }

  /**
   * Unsubscribe from stream
   */
  public async unsubscribe(): Promise<void> {
    this.log.debug('unsubscribe subscription')
    await this.tcpConnection.unsubscribeFromStream(this.id)
  }

  /**
   * Called when subscription was dropped
   */
  protected onDropped(
    reason: model.eventstore.proto.SubscriptionDropped.SubscriptionDropReason
  ): void {
    this.log.debug({reason}, 'Subscription dropped')
  }

  /**
   * Determines whether event on
   */
  protected onEvent(event: Event, position: Position): void {
    this.position = position
    this.log.debug({eventName: event.name, eventId: event.id}, 'Event received')
  }

  /**
   * Called when subscription is established
   */
  protected onSubscribed(): void {
    this.log.debug(
      {subscriptionId: this.subscriptionId, stream: this.stream.id},
      'Subscription started'
    )
  }

  /**
   * Called when error appears
   */
  protected onError(err: Error): void {
    this.log.error({err, subscriptionId: this.subscriptionId}, 'Error on subscription')
  }
}

# Eventstore

## connecting

Main connection parameters are set inside of connection uri  
This lib supports connecting to single instance via tcp and auto discovering to clusters also.  
Connections can be unsecured or encrypted

```javascript
// connection to single instance at local at port 1113 with username and password
const es = new Eventstore({
  uri: 'tcp://username:userpassword@127.0.0.1:1113'
})

await es.connect()
```

```javascript
// connection to cluster at domain escluster.net at port 2112 with username and password
const es = new Eventstore({
  uri: 'discover://username:password@escluster.net:2112'
})

await es.connect()

//...same with some additional properties like encryption and connect to master only
const es = new Eventstore({
  uri: 'discover://username:password@escluster.net:2112',
  requireMaster:true,
  useSSL: true,
  validateServer: true,
  secureContext: {
    ca: fs.readFileSync('./rootCA.crt'),
    key: fs.readFileSync('./domain.key'),
    cert: fs.readFileSync('./domain.crt')
  }
})

await es.connect()
```

## disconnecting

It's highly recommended to close a connection in save way.  
You should use `es.disconnect()` for proper shut down, because this function sets current connection to state `drain`.  
This means the connection does not accept outgoing requests any longer and waits if necessary for outstanding responses from eventstore.

